#ifndef MSORT_API_H
#define MSORT_API_H

#ifdef HAVE_CONFIG_H
#  include "config.h"
#endif

#include <stdlib.h>
#include "core/fptr_api.h"

/* Msort module */

/* Sorts an array of <nmemb> elements, each of size <size>, according to compare
   function <compar>. Uses the merge sort algorithm, the interface equals
   <qsort(3)>. */
void PPM_msort(void *base, size_t nmemb, size_t size, PPM_Compare compar);

/* Identical to <PPM_msort()> except that the compare function is of
   <PPM_CompareWithData> type accepting <comparinfo> as arbitrary data. */
void PPM_msort_r(void *base, size_t nmemb, size_t size, void *comparinfo,
                 PPM_CompareWithData compar);

#endif
