#ifndef QSORT_R_API_H
#define QSORT_R_API_H

#ifdef HAVE_CONFIG_H
#  include "config.h"
#endif

#include <stdlib.h>

#include "core/fptr_api.h"

/* Qsort module */

/* Like <qsort(3)>, but allows an additional <data> pointer passed to the
   <PPM_CompareWithData> comparison function <cmp>. */
void PPM_qsort_r(void *a, size_t n, size_t es, void *data,
                 PPM_CompareWithData cmp);
/* same as PPM_qsort_r but uses tasks to split work */
void
PPM_qsort_r_mt(void *a, size_t n, size_t es, void *data,
               PPM_CompareWithData cmp);

#endif
